import * as React from "react";

import { View, Image, TouchableOpacity, Text } from "react-native";

import { useNavigation } from "@react-navigation/native";

import { AntDesign } from "@expo/vector-icons";

import GlobalVars from "../../../global/globalVars";

/** Import Custom elements */

import Styles from "./style";

const styles = Styles;

const ResultSearchCard = ({ itemProduct, ...props }) => {
  const navigation = useNavigation();
  const { onShow } = props;

  const goToProduct = () => {
    navigation.navigate("Product", { item: itemProduct });
    onShow();
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={styles.rootContainer}
        onPress={() => goToProduct()}
      >
        <View style={styles.viewcontainer}>
          <View style={styles.leftcontent}>
            <Image
              style={styles.tinyLogo}
              resizeMode="cover"
              source={{
                uri: itemProduct?.attributes?.image?.data?.attributes?.uri,
              }}
            />
          </View>
          <View style={styles.rightcontent}>
            <View style={styles.headercard} />
            <View style={styles.contentcard}>
              <Text style={styles.nameproduct}>
                {itemProduct?.attributes?.product.length > 15
                  ? itemProduct?.attributes?.product.slice(0, 15) + "..."
                  : itemProduct?.attributes?.product || ""}
              </Text>
              <TouchableOpacity
                style={styles.iconView}
                onPress={() => goToProduct()}
              >
                <AntDesign
                  name="rightcircleo"
                  size={24}
                  color={GlobalVars.blueOpaque}
                />
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </TouchableOpacity>
    </View>
  );
};

export default ResultSearchCard;
