import * as React from "react";
import { View } from "react-native";

import { MaterialCommunityIcons, Feather } from "@expo/vector-icons";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

/** Screens */
import StartStack from "./StartStack";
import ProfileStack from "./ProfileStack";
import Notifications from "../../screens/Notifications";

/** Global vars */
import GlobalVars from "../../global/globalVars";

import Styles from "./style";

const styles = Styles;

const Tab = createBottomTabNavigator();
function TabBottom() {
  return (
    <Tab.Navigator
      initialRouteName="Inicio"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ color, focused }) =>
          screenOptions(route, color, focused),
        // tabBarActiveTintColor: GlobalVars.firstColor,
        // tabBarInactiveTintColor: '#EEEEEE',
        tabBarShowLabel: false,
        tabBarStyle: [
          {
            display: "flex",
            // backgroundColor: GlobalVars.firstColor,
          },
          styles.tabBarStyle,
        ],
        headerShown: false,
        // tabBarItemStyle: [
        //   {
        //     backgroundColor: 'transparent',
        //   },
        // ],
        tabBarHideOnKeyboard: true,
      })}
    >
      <Tab.Screen name="Inicio" component={StartStack} />
      <Tab.Screen name="Notifications" component={Notifications} />
      <Tab.Screen name="Profile" component={ProfileStack} />
    </Tab.Navigator>
  );
}

function screenOptions(route, color, focused) {
  let iconName, type;
  switch (route.name) {
    case "Inicio":
      iconName = "home-variant-outline";
      type = "materialcomm";
      break;

    case "Profile":
      iconName = "user";
      type = "feather";
      break;

    case "Notifications":
      iconName = "message-badge-outline";
      type = "materialcomm";
      break;
  }

  if (type === "materialcomm") {
    if (focused) {
      return (
        <View style={styles.viewOptions}>
          <View style={styles.viewContent}>
            <MaterialCommunityIcons
              name={iconName}
              size={28}
              color={GlobalVars.orange}
            />
          </View>
        </View>
      );
    } else {
      return (
        <MaterialCommunityIcons
          name={iconName}
          size={20}
          color={GlobalVars.blueOpaque}
        />
      );
    }
  }

  if (type === "feather") {
    if (focused) {
      return (
        <View style={styles.viewOptions}>
          <View style={styles.viewContent}>
            <Feather name={iconName} size={28} color={GlobalVars.orange} />
          </View>
        </View>
      );
    } else {
      return (
        <Feather name={iconName} size={20} color={GlobalVars.blueOpaque} />
      );
    }
  }
}

export default TabBottom;
