import React from "react";
import { TouchableOpacity, View } from "react-native";

import { Feather } from "@expo/vector-icons";

import LabelTextComponent from "../../atoms/LabelText";

import styles from "./style";

const OptionTouchable = ({
  text,
  colorText,
  sizeText,
  colorIcon,
  sizeIcon,
  iconOption,
  aditionalStyle,
  onPress,
  customChevronStyle = false,
  ...props
}) => {
  if (!text || !colorText || !sizeText || !colorIcon || !sizeIcon || !onPress)
    return <></>;

  return (
    <TouchableOpacity
      onPress={onPress}
      style={[styles.touchableItem, aditionalStyle || null]}
    >
      {iconOption && (
        <Feather
          style={styles.iconOptionStyle}
          name={iconOption}
          size={sizeIcon}
          color={colorIcon}
        />
      )}

      {customChevronStyle && (
        <View style={styles.paddingRight}>
          <LabelTextComponent text={text} color={colorText} size={sizeText} />
        </View>
      )}

      {!customChevronStyle && (
        <LabelTextComponent text={text} color={colorText} size={sizeText} />
      )}

      <Feather
        style={[styles.iconstyle, customChevronStyle || null]}
        name="chevron-right"
        size={sizeIcon}
        color={colorIcon}
      />
    </TouchableOpacity>
  );
};

export default OptionTouchable;
