import React, { useState, useEffect } from "react";

import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";

import { Entypo, Feather } from "@expo/vector-icons";

import * as ImagePicker from "expo-image-picker";

import GlobalVars from "../../../global/globalVars";

import { GET_ENTREPRENEUR_BY_ID } from "../../../mock/entrepreneurById";

import productData from "../../../helpers/dataProduct";

import LabelTextComponent from "../../atoms/LabelText";
import ButtonComponent from "../../atoms/ButtonComponent";
import ImageUriComponent from "../../atoms/ImageUriComponent";
import ScrollView from "../../templates/ScrollView";

import Styles from "./style";

const styles = Styles;

const PickerAddProduct = ({
  isEntrepreneurUpdate = false,
  _jwt = null,
  entrepreneur = null,
  ...props
}) => {
  const { onClose, getRandomNumberDispatch } = props;

  const [dataShop, setDataShop] = useState(null);
  const [unity, setUnity] = useState("unidad");
  const [qty, setQty] = useState(1);
  const [price, setPrice] = useState(0);
  const [description, setDescription] = useState("");
  const [delivery, setDelivery] = useState(false);
  const [isStarProduct, setIsStarProduct] = useState(false);

  const [filePath, setFilePath] = useState(null);

  const [errMsg, setErrMsg] = useState("");
  const [showErr, setShowErr] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let isMount = true;

    if (isMount) {
      // Data in CMS
      onRecoverData();
    }

    return () => {
      isMount = false;
    };
  }, []);

  const onRecoverData = async () => {
    const res = await GET_ENTREPRENEUR_BY_ID(_jwt, entrepreneur?.id);
    setDataShop(res);
  };

  const getMimeType = (ext) => {
    // mime type mapping for few of the sample file types
    switch (ext) {
      case "jpg":
        return "image/jpeg";
      case "jpeg":
        return "image/jpeg";
      case "png":
        return "image/png";
    }
  };

  const requestCameraPermission = async () => {
    try {
      const permissionResult =
        await ImagePicker.requestCameraPermissionsAsync();
      return permissionResult;
    } catch (err) {
      // console.warn(err);
      setErrMsg("Error de permisos de cámara");
      setShowErr(true);
      setTimeout(() => {
        setShowErr(false);
      }, 1200);
      return false;
    }
  };

  const requestMediaLibraryPermission = async () => {
    try {
      const permissionResult =
        await ImagePicker.requestMediaLibraryPermissionsAsync();
      return permissionResult;
    } catch (err) {
      // console.warn(err);
      setErrMsg("Error de permisos de cámara");
      setShowErr(true);
      setTimeout(() => {
        setShowErr(false);
      }, 1200);
      return false;
    }
  };

  const captureImage = async () => {
    const isCameraPermitted = await requestCameraPermission();
    const isStoragePermitted = await requestMediaLibraryPermission();
    if (isCameraPermitted?.granted && isStoragePermitted?.granted) {
      const result = await ImagePicker.launchCameraAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        // aspect: [4, 3],
        quality: 1,
      });

      const fileUri = result?.uri;
      let filename = fileUri?.split("/").pop() || "";
      const extArr = /\.(\w+)$/.exec(filename);
      const type = getMimeType(extArr[1]);

      if (!result?.cancelled) {
        setFilePath(
          result?.uri
            ? {
                uri: result?.uri || "",
                type: result?.type || "",
                width: result?.width || 0,
                height: result?.height || 0,
                format: type || "",
              }
            : null
        );
      } else {
        setErrMsg("El usuario ha cancelado la carga.");
        setShowErr(true);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      }
    }
  };

  const chooseFile = async () => {
    const isStoragePermitted = await requestMediaLibraryPermission();
    if (isStoragePermitted?.granted) {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        // allowsEditing: true,
        // aspect: [4, 3],
        quality: 1,
      });

      const fileUri = result?.uri;
      let filename = fileUri?.split("/").pop() || "";
      const extArr = /\.(\w+)$/.exec(filename);
      const type = getMimeType(extArr[1]);

      if (!result?.cancelled) {
        setFilePath(
          result?.uri
            ? {
                uri: result?.uri || "",
                type: result?.type || "",
                width: result?.width || 0,
                height: result?.height || 0,
                format: type || "",
              }
            : null
        );
      } else {
        setErrMsg("El usuario ha cancelado la carga.");
        setShowErr(true);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      }
    }
  };

  const saveProduct = async () => {
    try {
      setLoading(true);
      if (!description) {
        setErrMsg("Ingrese una descripción para el producto.");
        setShowErr(true);
        setLoading(false);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      } else if (!entrepreneur?.id) {
        setErrMsg("Datos incorrectos, revise su conexión.");
        setShowErr(true);
        setLoading(false);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      } else if (typeof delivery !== "boolean") {
        setErrMsg("Error de red, salga e ingrese de nuevo en la aplicación.");
        setShowErr(true);
        setLoading(false);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      } else if (typeof isStarProduct !== "boolean") {
        setErrMsg("Error de red, salga e ingrese de nuevo en la aplicación.");
        setShowErr(true);
        setLoading(false);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      } else if (!filePath) {
        setErrMsg("No ha cargado ninguna imagen al producto.");
        setShowErr(true);
        setLoading(false);
        setTimeout(() => {
          setShowErr(false);
        }, 1200);
      } else {
        const dataSend = {
          description: description,
          qty: qty,
          unity: unity,
          price: price,
          shop: entrepreneur?.id,
          delivery: delivery,
          starProduct: isStarProduct,
          filePath: filePath,
        };

        const res = await productData.addProduct(
          {
            product: dataSend,
          },
          _jwt
        );

        if (res) {
          setLoading(false);
          onClose();
          getRandomNumberDispatch();
        } else {
          setErrMsg("Ocurrió un error durante el registro de datos.");
          setShowErr(true);
          setTimeout(() => {
            setShowErr(false);
          }, 1200);
          setLoading(false);
        }
      }
    } catch (e) {
      setErrMsg("Ocurrió un error durante el registro de datos.");
      setShowErr(true);
      setLoading(false);
      setTimeout(() => {
        setShowErr(false);
      }, 1200);
    }
  };

  return (
    <View style={styles.view}>
      {!loading && (
        <LabelTextComponent
          text="Crear producto"
          color={GlobalVars.blueOpaque}
          size={20}
        />
      )}

      {loading && (
        <View
          style={{
            width: "100%",
            height: GlobalVars.windowHeight / 2,
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <ActivityIndicator color={GlobalVars.orange} size="large" />
        </View>
      )}

      {showErr && errMsg && (
        <TouchableOpacity onPress={() => setShowErr(false)}>
          <LabelTextComponent
            text={errMsg}
            color={GlobalVars.googleColor}
            size={13}
          />
        </TouchableOpacity>
      )}

      {!loading && (
        <View style={styles.containerScroll}>
          <ScrollView
            style={styles.scrolling}
            contentContainerStyle={styles.wrapScrollView}
            colorScrollBar={GlobalVars.orange}
          >
            <TouchableOpacity style={styles.unityView} activeOpacity={1}>
              <LabelTextComponent
                text="Producto"
                color={GlobalVars.textGrayColor}
                size={13}
              />
              <View style={styles.setBoxes}>
                <TextInput
                  style={styles.inputDescription}
                  onChangeText={(val) => setDescription(val)}
                  value={description}
                  placeholder="Nombre del producto *"
                  keyboardType="default"
